const Research = () => {
  return (
    <div style={{ minHeight: '80vh', backgroundColor: '#222', color: '#fff', padding: '2rem' }}>
      <h1 style={{ color: '#ff6600', fontSize: '2.5rem', marginBottom: '1rem' }}>Research Work</h1>
      <h3 style={{ color: '#fff' }}>Enhancing Fairness and Interpretability in Electroencephalogram-Based Post-Traumatic Stress Disorder Diagnosis with Explainable Artificial Intelligence</h3>
      <p style={{ color: '#ccc', fontSize: '1.2rem', lineHeight: '1.8' }}>
        Pioneered the use of Explainable AI (XAI) in Electroencephalogram (EEG) analysis to transform Post-Traumatic Stress Disorder (PTSD) diagnosis into a transparent and trustworthy process.<br/>
        Identified a critical neurophysiological biomarker for PTSD: Beta Power Spectral Density in the Right Primary Motor Cortex, linking the disorder directly to heightened arousal and sensory impairments.<br/>
        Conducted a crucial Fairness Analysis on the diagnostic framework, exposing significant demographic biases across groups (sex, education) and establishing a necessity for equitable AI in psychiatric healthcare.<br/>
        <a href="#" style={{ color: '#ff6600' }}>Link</a>
      </p>
    </div>
  );
};

export default Research;
