import React from 'react';

const Research = () => {
  const items = [
    {
      id: 'eeg-ptsd',
      title: 'EEG-based PTSD Diagnosis',
      summary:
        'Interpretable ML pipeline for PTSD diagnosis using EEG features. Focus on robust feature extraction, explainability and clinician-friendly outputs.',
      href: '/research/eeg-ptsd',
      tags: ['EEG', 'ML', 'Healthcare'],
      date: '2024',
    },
    {
      id: 'brain-tumor',
      title: 'Brain Tumor Detection',
      summary:
        'Medical-imaging workflow using convolutional networks and attention maps to highlight suspicious regions — built for clinician review and validation.',
      href: '/research/brain-tumor',
      tags: ['Imaging', 'CNN', 'Segmentation'],
      date: '2023',
    },
  ];

  return (
    <section id="research" className="container page">
      <h2 className="page-title">Research & Selected Projects</h2>
      <p className="lead">Work combining machine learning, medical imaging and interpretable AI.</p>

      <div className="research-grid" role="list">
        {items.map((it) => (
          <article key={it.id} className="research-card card" role="listitem">
            <div className="card-head">
              <h3 className="card-title">{it.title}</h3>
              <time className="muted" dateTime={it.date}>{it.date}</time>
            </div>

            <p className="summary">{it.summary}</p>

            <div style={{ marginTop: 'auto', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div style={{ display: 'flex', gap: 8 }}>
                {it.tags.map((t) => (<span key={t} style={{ background: 'rgba(249,115,22,0.07)', color: 'var(--accent)', padding: '6px 10px', borderRadius: 999, fontWeight: 700, fontSize: '0.85rem' }}>{t}</span>))}
              </div>

              <a className="btn" style={{ border: '1px solid var(--accent)', color: 'var(--accent)', background: 'transparent', borderRadius: 10, padding: '8px 12px', fontWeight: 700, textDecoration: 'none' }} href={it.href}>Read</a>
            </div>
          </article>
        ))}
      </div>
    </section>
  );
};

export default Research;
