import { useState } from 'react';

const Contact = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Thank you for your message! I will get back to you soon.');
    setFormData({ name: '', email: '', message: '' });
  };

  return (
    <div className="page">
      <div className="container center">
        <h1 className="page-title">Get in Touch</h1>
        <p className="lead">I'd love to hear from you! Fill out the form below or reach out via my contact details.</p>

        <form onSubmit={handleSubmit} className="card" style={{ maxWidth: 640, margin: '0 auto' }}>
          <div style={{ marginBottom: '1rem' }}>
            <label className="label">Name</label>
            <input className="input" type="text" name="name" value={formData.name} onChange={handleChange} required />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label className="label">Email</label>
            <input className="input" type="email" name="email" value={formData.email} onChange={handleChange} required />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label className="label">Message</label>
            <textarea className="input" name="message" value={formData.message} onChange={handleChange} required style={{ height: 120 }} />
          </div>

          <div style={{ display: 'flex', justifyContent: 'flex-start', gap: 12 }}>
            <button type="submit" className="btn primary">Send Message</button>
            <a className="link" href="mailto:connect.darshanparmar@gmail.com">Email me</a>
          </div>
        </form>

        <div style={{ marginTop: '2.5rem', maxWidth: 640, marginLeft: 'auto', marginRight: 'auto' }} className="card">
          <h3 style={{ color: 'var(--accent)', marginTop: 0 }}>Contact Information</h3>
          <p className="muted">Email: <a className="link" href="mailto:connect.darshanparmar@gmail.com">connect.darshanparmar@gmail.com</a></p>
          <p className="muted">Phone: +91 8141902382</p>
          <p className="muted">Location: Bharuch, Gujarat</p>
          <div style={{ marginTop: '0.75rem' }}>
            <a className="link" href="https://linkedin.com/in/dte-gecbh-com-darshan-parmar" target="_blank" rel="noreferrer">LinkedIn</a>
            <span style={{ margin: '0 0.5rem', color: 'var(--muted)' }}>Â·</span>
            <a className="link" href="https://github.com/ParmarDarshan29" target="_blank" rel="noreferrer">GitHub</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
