const Research = () => {
  return (
    <div style={{ minHeight: '80vh', backgroundColor: '#666', color: '#fff', padding: '2rem', animation: 'fadeIn 1s ease-in' }}>
      <h1 style={{ color: '#ff6600', fontSize: '2.5rem', marginBottom: '1rem', textAlign: 'center', transition: 'color 0.3s ease' }} onMouseEnter={(e) => e.target.style.color = '#e55a00'} onMouseLeave={(e) => e.target.style.color = '#ff6600'}>Research Work</h1>
      <div style={{ maxWidth: '800px', margin: '0 auto', backgroundColor: '#555', padding: '2rem', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0,0,0,0.2)', transition: 'transform 0.3s ease' }} onMouseEnter={(e) => e.target.style.transform = 'scale(1.02)'} onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}>
        <h3 style={{ color: '#ff6600' }}>Enhancing Fairness and Interpretability in Electroencephalogram-Based Post-Traumatic Stress Disorder Diagnosis with Explainable Artificial Intelligence</h3>
        <p style={{ color: '#ddd', fontSize: '1.1rem', lineHeight: '1.8' }}>
          Pioneered the use of Explainable AI (XAI) in Electroencephalogram (EEG) analysis to transform Post-Traumatic Stress Disorder (PTSD) diagnosis into a transparent and trustworthy process.<br/>
          Identified a critical neurophysiological biomarker for PTSD: Beta Power Spectral Density in the Right Primary Motor Cortex, linking the disorder directly to heightened arousal and sensory impairments.<br/>
          Conducted a crucial Fairness Analysis on the diagnostic framework, exposing significant demographic biases across groups (sex, education) and establishing a necessity for equitable AI in psychiatric healthcare.<br/>
          <a href="#" style={{ color: '#ff6600', textDecoration: 'none', transition: 'color 0.3s ease' }} onMouseEnter={(e) => e.target.style.color = '#e55a00'} onMouseLeave={(e) => e.target.style.color = '#ff6600'}>View Research</a>
        </p>
      </div>
      <style>
        {`
          @keyframes fadeIn {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
          }
        `}
      </style>
    </div>
  );
};

export default Research;
