import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const STORAGE_KEY = 'portfolio_projects_v1';
const AUTH_KEY = 'portfolio_admin_auth_v1';
const ENV_PASS = process.env.REACT_APP_ADMIN_PASSWORD || '';

function loadProjects() {
  try {
    const raw = localStorage.getItem(STORAGE_KEY);
    return raw ? JSON.parse(raw) : [];
  } catch {
    return [];
  }
}

function saveProjects(list) {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(list));
}

export default function Admin() {
  const [authenticated, setAuthenticated] = useState(false);
  const [password, setPassword] = useState('');
  const [projects, setProjects] = useState([]);
  const [form, setForm] = useState({ id: '', title: '', desc: '', tags: '', url: '', image: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const ok = sessionStorage.getItem(AUTH_KEY) === '1';
    setAuthenticated(ok);
    if (ok) setProjects(loadProjects());
  }, []);

  const login = (e) => {
    e.preventDefault();
    // Compare raw password to env var (set REACT_APP_ADMIN_PASSWORD). Client-side only.
    if (!ENV_PASS) {
      alert('Admin password is not set. Create REACT_APP_ADMIN_PASSWORD in .env and restart.');
      return;
    }
    if (password === ENV_PASS) {
      sessionStorage.setItem(AUTH_KEY, '1');
      setAuthenticated(true);
      setProjects(loadProjects());
      setPassword('');
    } else {
      alert('Invalid password');
    }
  };

  const logout = () => {
    sessionStorage.removeItem(AUTH_KEY);
    setAuthenticated(false);
    navigate('/');
  };

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const submitProject = (e) => {
    e.preventDefault();
    const payload = {
      id: form.id || Date.now().toString(36),
      title: form.title,
      desc: form.desc,
      tags: form.tags ? form.tags.split(',').map(t => t.trim()).filter(Boolean) : [],
      url: form.url,
      image: form.image,
    };
    const next = [...projects.filter(p => p.id !== payload.id), payload];
    setProjects(next);
    saveProjects(next);
    setForm({ id: '', title: '', desc: '', tags: '', url: '', image: '' });
  };

  const edit = (p) => setForm({ id: p.id, title: p.title, desc: p.desc, tags: (p.tags || []).join(', '), url: p.url || '', image: p.image || '' });

  const remove = (id) => {
    if (!confirm('Remove this project?')) return;
    const next = projects.filter(p => p.id !== id);
    setProjects(next);
    saveProjects(next);
  };

  if (!authenticated) {
    return (
      <section className="container page">
        <h2 className="page-title">Admin — Sign in</h2>

        {/* required message for visitors */}
        <p className="lead">
          This area is for Darshan Parmar only — you can explore other parts of the site freely.
        </p>

        <p className="muted" style={{ marginBottom: 12 }}>
          Enter the admin password to manage projects. The admin route is intentionally minimal and protected by a password stored in the environment.
        </p>

        <form onSubmit={login} className="card" style={{ maxWidth: 560 }}>
          <label className="label">Password</label>
          <input className="input" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          <div style={{ marginTop: 12 }}>
            <button className="btn primary" type="submit">Sign in</button>
          </div>
        </form>
      </section>
    );
  }

  return (
    <section className="container page">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2 className="page-title">Admin — Projects</h2>
        <div>
          <button className="btn" onClick={logout}>Sign out</button>
        </div>
      </div>

      <div style={{ display: 'grid', gap: 18, marginTop: 16 }}>
        <form onSubmit={submitProject} className="card" style={{ maxWidth: 800 }}>
          <h3 style={{ marginTop: 0 }}>{form.id ? 'Edit project' : 'Add project'}</h3>

          <label className="label">Title</label>
          <input className="input" name="title" value={form.title} onChange={handleChange} required />

          <label className="label" style={{ marginTop: 8 }}>Description</label>
          <textarea className="input" name="desc" value={form.desc} onChange={handleChange} required style={{ minHeight: 80 }} />

          <label className="label" style={{ marginTop: 8 }}>Tags (comma separated)</label>
          <input className="input" name="tags" value={form.tags} onChange={handleChange} />

          <label className="label" style={{ marginTop: 8 }}>Project URL (GitHub / live)</label>
          <input className="input" name="url" value={form.url} onChange={handleChange} />

          <label className="label" style={{ marginTop: 8 }}>Image URL (optional)</label>
          <input className="input" name="image" value={form.image} onChange={handleChange} />

          <div style={{ marginTop: 12 }}>
            <button className="btn primary" type="submit">{form.id ? 'Save changes' : 'Add project'}</button>
          </div>
        </form>

        <div>
          <h3 style={{ marginBottom: 8 }}>Existing projects</h3>
          <div style={{ display: 'grid', gap: 12 }}>
            {projects.length === 0 && <div className="card"><p className="muted">No projects yet.</p></div>}
            {projects.map(p => (
              <div key={p.id} className="card" style={{ display: 'flex', justifyContent: 'space-between', gap: 12, alignItems: 'center' }}>
                <div>
                  <strong>{p.title}</strong>
                  <div className="muted" style={{ marginTop: 6 }}>{(p.tags || []).join(', ')}</div>
                  <div style={{ marginTop: 6 }}>
                    {p.url ? <a className="link" href={p.url} target="_blank" rel="noreferrer">Link</a> : <span className="muted">No link</span>}
                  </div>
                </div>

                <div style={{ display: 'flex', gap: 8 }}>
                  <button className="btn" onClick={() => edit(p)}>Edit</button>
                  <button className="btn" onClick={() => remove(p.id)}>Delete</button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}
